mapper.select:
select * from member
where m_id = #{m_id}
and m_pw = #{m_pw}

mapper.update:
update member set 
m_blackpoint = m_blackpoint + #{black_score}
where m_id = #{black_m_id}

mapper.select:
select m.m_id, m.m_name, m.gender, m.m_dept, d.driver_depart_location, d.driver_depart_time, d.driver_comment
from member m, driver d
where m.m_id = d.m_id
and m.m_company = #{m_company}
and m.m_id = #{m_id}

mapper.update:
update member set
m_address = #{m_address},
m_cellphone = #{m_cellphone},
m_is_drop = #{m_is_drop}
where m_id = #{m_id}

mapper.select:
update passenger set
is_deletion = 'Y'
where m_id = #{m_id}
and driver_seq = #{driver_seq}
and is_approve = 'W'

select m.m_name, m.m_dept, p.passenger_depart_time, p.passenger_depart_location
	from member m, passenger p
	where m.m_id = p.m_id
	and p.is_deletion = 'N'
	and p.driver_seq = #{driver_seq}
and m_company = #{m_company}
